var ndb = require("ndb");

exports.ndb = ndb;

exports.commandNames = ["tinyfuncs"];

exports.parseCommand = function(text) {
  return this.ndb.Helpers.commonParse.call(this, text);
};

function toggle(obj, value) {
  obj[value] = !obj[value];
}


exports.run = function() {
  var ndb    = this.ndb,
      puts   = ndb.Helpers.puts,
      prompt = ndb.Helpers.prompt;

  toggle(ndb, "tinyfuncs");
  if (ndb.tinyfuncs)
    puts("Functions now displayed shortened, like function () {...}");
  else
    puts("Functions now displayed in full");
  prompt();
};
